INSERT INTO disciplina(nome, ementa) VALUES('Pratica em Engenharia de Software','Especificação, desenvolvimento, manutenção e criação de software, com a aplicação de tecnologias e práticas de gerência de projetos e outras disciplinas, visando organização, produtividade e qualidade.');
INSERT INTO disciplina(nome, ementa) VALUES('Fundamentos de Inteligencia Artificial','Inteligência demonstrada por máquinas ao executar tarefas complexas associadas a seres inteligentes.');
INSERT INTO disciplina(nome, ementa) VALUES('Criatividade','Tornar-se sensível a problemas, deficiências, lacunas no conhecimento, desarmonia; identificar a dificuldade, buscar soluções, formulando hipóteses a respeito das deficiências; testar e retestar estas hipóteses; e, finalmente, comunicar os resultados.');
INSERT INTO disciplina(nome, ementa) VALUES('Laboratorio de Redes','Rede de computadores ou redes de dados, na informática e na telecomunicação é um conjunto de dois ou mais dispositivos eletrônicos de computação (ou módulos processadores ou nós da rede) interligados por um sistema de comunicação digital.');
INSERT INTO disciplina(nome, ementa) VALUES('Analise de Dados Avançada','A análise de dados é um processo de inspeção, limpeza, transformação e modelagem de dados com o objetivo de descobrir informações úteis.');
INSERT INTO disciplina(nome, ementa) VALUES('Caluclo','O cálculo infinitesimal, também conhecido como cálculo diferencial e integral ou simplesmente cálculo, é um ramo importante da matemática, desenvolvido a partir da Álgebra e da Geometria, que se dedica ao estudo de taxas de variação de grandezaz e a acumulação de quantidades.');
INSERT INTO disciplina(nome, ementa) VALUES('Metodos Formais','Técnicas baseadas em formalismos matemáticos para a especificação, desenvolvimento e verificação dos sistemas de softwares e hardwares.');
INSERT INTO disciplina(nome, ementa) VALUES('Business Inteligence','Business Intelligence refere-se ao processo de coleta, organização, análise, compartilhamento e monitoramento de informações que oferecem suporte a gestão de negócios.');
